import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useMediaQuery } from 'react-responsive';
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';
import { Link } from 'react-router-dom';



export const Owl = ({ mentorListObj }) => {
    return (
        <div className='container mt-5'>
            <OwlCarousel
                className='owl-theme owl-prev owl-next'
                loop margin={10}
                items="4"
                autoplay
                nav>
                {mentorListObj.map((mentorObj, index) => {
                    return (
                        <div className='item'>
                            <div className='image'>
                                <img src={mentorObj.imageURL} className='olc image_img' alt="..." />
                                <div className='image_overlay bg-light'>
                                    <div className='image_title'>{mentorObj.name}</div>
                                    <p className='image_description'>{mentorObj.tagLine}</p>
                                </div>
                            </div>
                            <Link to={"/mentor/" + mentorObj.name} state={{ mentorObj: mentorObj, replace: false }} style={{ textDecoration: 'none' }}>
                            <a href="" className="ment_link ms-3 pt-2 mt-4 btn">Book a session with me!</a>
                            </Link>
                        </div>

                    )
                })}
            </OwlCarousel>
        </div>
    )
}
